package operators;

public class UnaryOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10;
		
		System.out.println("postIncrement="+a++);
		
		System.out.println("preIncrement="+(++a));
		
		System.out.println("postIncrement="+a--);
		
		System.out.println("predecrement="+--a);
		}
		

	}


